import React from 'react';

interface Props {
    addTodo: AddTodo;
    text: string
  }

export const AddTodoForm: React.FC<Props> = ({addTodo}) => {
  return (
    <form>
    <input
      type="text"
      value={text}
      onChange={e => {
        setText(e.target.value);
      }}
    />
    <button
      type="submit"
      onClick={e => {
        e.preventDefault();
        addTodo(text);
        setText('');
      }}
    >
      Add Todo
    </button>
  </form>
  );
};
